
public class Class6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		addition(10,20);
		
		Class6 p = new Class6();
		
		p.multipication(10,20);
		
}
	
	
	public static void addition(int x,int y)
	{
	
	int a = x ;
	int b = y ;
	int c = a+b ;
	
	System.out.println(c);
	}
	
	public void multipication(int x,int y)
	{
	
	int a = x ;
	int b = y ;
	int c = a*b ;
	
	System.out.println(c);
	}


	
}