package pkg1;

public class ClassJ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		for(int i=0; i<10; i++)
		{
			for(int j=10; j>(i+1); j--)
			{
				System.out.print("  ");
			}
			for(int k=0; k<(i+1); k++)
			{
				System.out.print(" *");
			}
			for(int l=1; l<(i+1); l++)
			{
				System.out.print(" *");
			}
			System.out.println();
		}
		for(int a=0; a<10; a++)
		{
			for(int b=0; b<(a+1); b++)
			{
				System.out.print("  ");
			}
			for(int c=10; c>(a+1); c--)
			{
				System.out.print(" *");
			}
			for(int d=9; d>(a+1); d--)
			{
				System.out.print(" *");
			}
			System.out.println();
			
		}}}
			
			
		




